import React, {useRef} from "react";
import { SubMenuListItem } from "./SubMenuListItem";


export function SubMenuList({subMenu}){
    const menu = useRef()
    
    React.useEffect(() => {
        subMenu.current = toogleMenu
    }, [])

    var toogleMenu = ()=> {
        menu.current.classList.toggle("sub-menu-options-show");
    };

    if(subMenu){
        return (
            <ul ref={menu} className="full-width menu-principal sub-menu-options">
                {subMenu.map((subMenuItem,index) =>(<SubMenuListItem key={index} menuSubItem={subMenuItem}/>))}
            </ul>
        )
    }
}
