import { getServerUrl } from "./configuration";

const URL = getServerUrl() + "/api/categoria/";
const KEY = "login.token"

export function findCategories(){
    //hacer el fetch al servidor 
    fetch(URL, {
        method: 'GET',
        headers: {
            'Authorization': 'BEARER ' + localStorage.getItem(KEY),
        },
    }).
    then(response => response.json()).
    then(content =>{
        if(content.status.message != null && content.status.message != ""){
            return content.status.message;
        }
    
        if(content.data != null){
            console.log(content.data)
            return content.data;
        }else{
            return "Error";
        }
    })
}
