import React, {useRef} from "react";
import {SubMenuList} from "./SubMenuList";
import { SubMenuListItem } from "./SubMenuListItem";

export function MenuListItem({menuItem}){
    const {text, icon, link, separator, submenus} = menuItem

    const subMenu = useRef();

    if(separator){
        return (<li className="full-width divider-menu-h"></li>)
    }

    if(submenus){
        var contenido = (
            <a href={link} className="full-width btn-subMenu">
                <div className="navLateral-body-cl">
                    <i className={"zmdi " + icon}></i>
                </div>
                <div className="navLateral-body-cr">
                    {text}
                </div>
                <span className="zmdi zmdi-chevron-left"></span>
            </a>
        )
    }else{
        var contenido = (
            <a href={link} className="full-width">
                <div className="navLateral-body-cl">
                    <i className={"zmdi " + icon}></i>
                </div>
                <div className="navLateral-body-cr">
                    {text}
                </div>
            </a>
        )
    }

    if(submenus){
        console.log(submenus)
        return(
            <li className="full-width">
                <a href={link} className="full-width btn-subMenu">
                    <div className="navLateral-body-cl">
                        <i className={"zmdi " + icon}></i>
                    </div>
                    <div className="navLateral-body-cr">
                        {text}
                    </div>
                    <span className="zmdi zmdi-chevron-left"></span>
                </a>
                <SubMenuList ref={subMenu} subMenu={submenus}/>
            </li>
        )
    }

    const menuSubItem = {
        text: text,
        icon: icon, 
        link: link
    }

    return <SubMenuListItem menuSubItem={menuSubItem}/>
}
